import React, {useState, useEffect} from 'react';
import authorStore from '../stores/authorStore';
import AuthorList from './AuthorList';
import { Link } from 'react-router-dom';
import { loadAuthors } from "../actions/authorActions";

function AuthorsPage() {

    const [authors, setAuthors] = useState(authorStore.getAuthors());

    useEffect(() => {
        authorStore.addChangeListener(onChange);
        if(authorStore.getCourses().length === 0) {
            loadAuthors();
        }
        return () => authorStore.removeChangeListener(onChange);
    }, []);

    function onChange() {
        setAuthors(authorStore.getAuthors());
    }

    return(
        <>
            <h2>Authors</h2>
            <Link className="btn btn-primary" to="/author">
                Add Author
            </Link>
            <AuthorList authors={authors}/>
            <CourseList courses={courses} deleteCourse={deleteAuthor} getAuthorNameById={getAuthorNameById}/>
        </>
    );

}

export default AuthorsPage;

